	padding: 40px 3%;
}
/*トップページ以外のcontentsの追加設定*/
header + #menubar-s + .contents {
	padding-top: 120px;	/*ヘッダーの高さを変えた場合はここを調整*/
}
/*コンテンツのh2タグの設定*/
.contents h2 {
	clear: both;
