.contents h2 + p,
.contents h3 + p {
	margin-top: -10px;
}

/*メインコンテンツ
---------------------------------------------------------------------------*/
.main {
	overflow: hidden;
	float: right;	/*右に回り込み*/
	width: 72%;		/*幅*/
	padding-bottom: 30px;
}

/*Serviceページの各ブロック
---------------------------------------------------------------------------*/
/*各ボックスの設定*/
.list {
	position: relative;
	overflow: hidden;
	padding: 20px;	/*ボックス内の余白*/
	border-bottom: 1px solid #dcdcdc;	/*下線の幅、線種、色*/
	margin: 0 20px;	/*上下、左右へのボックス外にあけるスペース*/
	line-height: 1.5;
}
/*１つ目のlistボックス（古いブラウザは適用されません）*/
.list:first-of-type {
	border-top: 1px solid #dcdcdc;	/*上の線の幅、線種、色*/
}
/*奇数番目のlistボックス*/
.list:nth-child(odd) {
	background: #fafafa;	/*奇数番目だけ背景色をつける*/
}
/*bg1の背景色のついたボックス内でのlistボックス*/
.bg1 .list {
	background: #fff;	/*背景色*/
}
/*ボックスにリンクをはった場合*/
.list a {
	text-decoration: none;
	display: block;
	overflow: hidden;
	padding: 20px;	/*ボックス内の余白。.listのpaddingと合わせる。*/
	margin: -20px;	/*.listのpaddingと二重になってしまう分をここでリセット。*/
}
/*マウスオン時の背景色*/
.list a:hover {
	background: #f9f9de;	/*背景色*/
}
/*リンクを貼った際に出る「→」マーク*/
.list a::before {
	content: "→";	/*出力する文字。変更しても構いませんが機種依存文字は化けるので使わないで下さい。*/
	font-size: 12px;	/*文字サイズ*/
	display: block;
	position: absolute;
	right: 10px;	/*ボックスの右から10pxの場所に配置*/
	bottom: 20px;	/*ボックスの下から20pxの場所に配置*/
